sysauto_script.script:
var grp = new GlideRecord('sys_user_group');
var groupDeptsCount = []; //two dimentional array to store the groups' departments with a count
var grpDepts = [];
var grpDeptsCount = [];
grp.addQuery('active', true);
grp.query();
while (grp.next()) {
    groupName = grp.getDisplayValue();
//	gs.print('GROUP = ' + GroupName);
	grpMem = new GlideRecord('sys_user_grmember');
	grpMem.addQuery('group', grp.sys_id);
	grpMem.addQuery('user.active', true);
	grpMem.query();

//	identify if group has 0 members and do something else do while loop

	while (grpMem.next()) {
		groupMemberName = grpMem.user.getDisplayValue();
		groupMemberDept = grpMem.user.department.getDisplayValue();
		gs.print('MEMBER = ' + groupMemberName + ' [' + groupMemberDept + ']');
		found = false;
		// increment counter for an existing dept
		for (var i = 0; i < grpDeptsCount.length; i++) {			
			if (grpDepts[i] == groupMemberDept) {
				found = true;
				grpDeptsCount[i] += 1;
				break;
			}			
		}
		// add a new dept to the array with a count of 1
		if (found == false) {
			grpDepts[i] = groupMemberDept;
			grpDeptsCount[i] = 1;
		}
	}
for (var j = 0; j < grpDeptsCount.length; j++) {
	gs.print(groupName + ' ------->  ' + grpDepts[j] + ' ======> ' + grpDeptsCount[j]);
}
	grpDepts= [];
        grpDeptsCount= []; 
	// identify dept with highest members and assign it to group as owning org

}
